import React, { Component } from "react";
import "./App.css";
import { Button } from "./components/Button";
import { Input } from "./components/Input";
import { ClearButton } from "./components/ClearButton";
import * as math from "mathjs";

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      input: "0"
    };
  }

  addToInput = val => {
    if (isNaN(val) && isNaN(this.state.input[this.state.input.length - 1])) {
      this.setState({ input: this.state.input + val });
    } else if (!this.state.input.match(/^0+/) && isNaN(val)) {
      this.setState({ input: this.state.input + val });
    } else {
      this.setState({ input: this.state.input.replace(/^0+/, "") + val });
    }
    console.log(this.state.input);
  };

  handleX = val => {
    if (isNaN(val) && isNaN(this.state.input[this.state.input.length - 1])) {
      this.setState({ input: this.state.input.replace(/(\+|\-|\*|\/)$/, "*") });
    } else {
      this.setState({ input: this.state.input + "*" });
    }
  };
  handleAdd = val => {
    if (isNaN(val) && isNaN(this.state.input[this.state.input.length - 1])) {
      this.setState({ input: this.state.input.replace(/(\+|\-|\*|\/)$/, "+") });
    } else {
      this.setState({ input: this.state.input + "+" });
    }
  };
  handleSubtract = val => {
    if (isNaN(val) && isNaN(this.state.input[this.state.input.length - 1])) {
      this.setState({ input: this.state.input.replace(/(\+|\-|\*|\/)$/, "-") });
    } else {
      this.setState({ input: this.state.input + "-" });
    }
  };
  handleDivide = val => {
    if (isNaN(val) && isNaN(this.state.input[this.state.input.length - 1])) {
      this.setState({
        input: this.state.input.replace(/(\+|\-|\*|\/)$/, "/")
      });
    } else {
      this.setState({ input: this.state.input + "/" });
    }
  };
  handleEqual = () => {
    let result = math.eval(this.state.input);

    this.setState({ input: result.toString() });
    console.log(typeof this.state.input);
  };
  handleDecimal = () => {
    if (math.eval(this.state.input + ".1")) {
      this.setState({ input: this.state.input + "." });
    } else if (!math.eval(this.state.input + ".1")) {
      this.setState({ input: this.state.input });
    }
  };
  handleZero = () => {
    var match2 = this.state.input.match(/^0/);
    var match3 = this.state.input.match(/\+0|\-0|\*0|\/0/);
    if (match2) {
      this.setState({ input: this.state.input });
    } else if (match3) {
      this.setState({ input: this.state.input });
    } else {
      this.setState({ input: this.state.input + "0" });
    }
  };

  render() {
    return (
      <div className="app">
        <div className="calc-wrapper">
          <fcc>
            <display id="display" className="input" input={this.state.input} />{" "}
            />
          </fcc>

          <div className="row">
            <fcc id="seven" className="button-wrapper">
              <Button handleClick={this.addToInput}>7</Button>
            </fcc>
            <fcc id="eight" className="button-wrapper">
              <Button handleClick={this.addToInput}>8</Button>
            </fcc>
            <fcc id="nine" className="button-wrapper">
              <Button handleClick={this.addToInput}>9</Button>
            </fcc>

            <fcc id="divide" className="button-wrapper">
              <Button handleClick={() => this.handleDivide()}>/</Button>
            </fcc>
          </div>
          <div className="row">
            <fcc id="four" className="button-wrapper">
              <Button handleClick={this.addToInput}>4</Button>
            </fcc>
            <fcc id="five" className="button-wrapper">
              <Button handleClick={this.addToInput}>5</Button>
            </fcc>
            <fcc id="six" className="button-wrapper">
              <Button handleClick={this.addToInput}>6</Button>
            </fcc>
            <fcc id="multiply" className="button-wrapper">
              <Button handleClick={() => this.handleX()}>X</Button>
            </fcc>
          </div>
          <div className="row">
            <fcc id="one" className="button-wrapper">
              <Button handleClick={this.addToInput}>1</Button>
            </fcc>
            <fcc id="two" className="button-wrapper">
              <Button handleClick={this.addToInput}>2</Button>
            </fcc>
            <fcc id="three" className="button-wrapper">
              <Button handleClick={this.addToInput}>3</Button>
            </fcc>
            <fcc id="add" className="button-wrapper">
              <Button handleClick={() => this.handleAdd()}>+</Button>
            </fcc>
          </div>
          <div className="row">
            <fcc className="button-wrapper">
              <button
                id="decimal"
                className="button-wrapper"
                onClick={() => this.handleDecimal()}
              >
                .
              </button>
            </fcc>

            <fcc id="zero" className="button-wrapper">
              <Button handleClick={() => this.handleZero()}>0</Button>
            </fcc>
            <fcc className="button-wrapper">
              <button
                id="equals"
                className="button-wrapper"
                onClick={() => this.handleEqual()}
              >
                =
              </button>
            </fcc>

            <fcc id="subtract" className="button-wrapper">
              <Button handleClick={() => this.handleSubtract()}>-</Button>
            </fcc>
          </div>
          <div className="row" id="clear">
            <ClearButton handleClear={() => this.setState({ input: "0" })}>
              Clear
            </ClearButton>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
