import React, { Component } from "react";
import "./App.css";
import { Button } from "./components/Button";
import { Input } from "./components/Input";
import { ClearButton } from "./components/ClearButton";
import * as math from "mathjs";

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      input: "0"
    };
  }

  addToInput = val => {
    if (isNaN(val) && isNaN(this.state.input[this.state.input.length - 1])) {
      this.setState({ input: this.state.input + val });
    } else if (!this.state.input.match(/^0+/) && isNaN(val)) {
      this.setState({ input: this.state.input + val });
    } else {
      this.setState({ input: this.state.input.replace(/^0+/, "") + val });
    }
  };

  handleX = val => {
    if (isNaN(val) && isNaN(this.state.input[this.state.input.length - 1])) {
      this.setState({
        input: this.state.input.slice(0, -1) + "*"
      });
    } else {
      this.setState({ input: this.state.input + "*" });
    }
  };
  handleAdd = val => {
    if (isNaN(val) && isNaN(this.state.input[this.state.input.length - 1])) {
      this.setState({ input: this.state.input.replace(/(\+|\-|\*|\/)$/, "+") });
    } else {
      this.setState({ input: this.state.input + "+" });
    }
  };
  handleSubtract = val => {
    if (isNaN(val) && isNaN(this.state.input[this.state.input.length - 1])) {
      this.setState({ input: this.state.input.replace(/(\+|\-|\*|\/)$/, "-") });
    } else {
      this.setState({ input: this.state.input + "-" });
    }
  };
  handleEqual = () => {
    this.setState({ input: math.eval(this.state.input) });
  };
  handleDecimal = () => {
    if (math.eval(this.state.input + ".1")) {
      this.setState({ input: this.state.input + "." });
    } else if (!math.eval(this.state.input + ".1")) {
      this.setState({ input: this.state.input });
    }

    console.log(this.state);
  };
  handleZero = () => {
    var match2 = this.state.input.match(/^0/);
    var match3 = this.state.input.match(/\+0|\-0|\*0|\/0/);
    if (match2) {
      this.setState({ input: this.state.input });
    } else if (match3) {
      this.setState({ input: this.state.input });
    } else {
      this.setState({ input: this.state.input + "0" });
    }
  };

  render() {
    return (
      <div className="app">
        <div className="calc-wrapper">
          <Input id="display" input={this.state.input} />
          <div className="row">
            <Button id="seven" handleClick={this.addToInput}>
              7
            </Button>
            <Button id="eight" handleClick={this.addToInput}>
              8
            </Button>
            <Button id="nine" handleClick={this.addToInput}>
              9
            </Button>
            <Button id="divide" handleClick={this.addToInput}>
              /
            </Button>
          </div>
          <div className="row">
            <Button id="four" handleClick={this.addToInput}>
              4
            </Button>
            <Button id="five" handleClick={this.addToInput}>
              5
            </Button>
            <Button id="six" handleClick={this.addToInput}>
              6
            </Button>
            <Button id="multiply" handleClick={() => this.handleX()}>
              X
            </Button>
          </div>
          <div className="row">
            <Button id="one" handleClick={this.addToInput}>
              1
            </Button>
            <Button id="two" handleClick={this.addToInput}>
              2
            </Button>
            <Button id="three" handleClick={this.addToInput}>
              3
            </Button>
            <Button id="add" handleClick={() => this.handleAdd()}>
              +
            </Button>
          </div>
          <div className="row">
            <Button id="decimal" handleClick={() => this.handleDecimal()}>
              .
            </Button>
            <Button id="zero" handleClick={() => this.handleZero()}>
              0
            </Button>
            <Button id="equals" handleClick={() => this.handleEqual()}>
              =
            </Button>
            <Button id="subtract" handleClick={() => this.handleSubtract()}>
              -
            </Button>
          </div>
          <div className="row">
            <ClearButton
              id="clear"
              handleClear={() => this.setState({ input: "0" })}
            >
              Clear
            </ClearButton>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
